const swiper=new Swiper(".swiper",{loop:!0,slidesPerView:"auto",autoplay:{delay:5e3,pauseOnMouseEnter:!0,disableOnInteraction:!1}});let hamburger=document.querySelector(".hamburger"),menu=document.querySelector(".menu"),menuclose=document.querySelector(".menu__close");hamburger.addEventListener("click",(()=>{menu.classList.add("active")})),menuclose.addEventListener("click",(()=>{menu.classList.remove("active")}));